using UnityEngine;using UnityEngine.SceneManagement;public class CharactorsKill:MonoBehaviour {    void Update() {        if (transform.position.y < -10) {            if (SceneManager.GetActiveScene().name == "1"|| SceneManager.GetActiveScene().name == "2") {                GameObject.Find("Player").GetComponent<PlayerClick>().EndGame();            }            Destroy(gameObject);        }    }}