using System.Collections;using UnityEngine;public class FallCharactors:MonoBehaviour {    public GameObject[] prefabs;    // Update is called once per frame    void Start() {        StartCoroutine(Coroutine());    }    IEnumerator Coroutine() {        while (true) {            var fallPoint = Camera.main.ViewportToWorldPoint(new Vector2(Random.Range(0.0f, 1.0f), 1)); fallPoint.z = 0;            var fallObj = prefabs[Random.Range(0, prefabs.Length)];
            fallObj.transform.localScale = new Vector3(1.0f, 1.0f, 1.0f);            Instantiate(fallObj, fallPoint, Quaternion.Euler(0.0f, 0.0f, Random.Range(0.0f, 360.0f)));            yield return new WaitForSeconds(1.5f);        }    }}