using UnityEngine;using UnityEngine.SceneManagement;using UnityEngine.UI;public class ResultController:MonoBehaviour {    public Text resultText;    void Start() {        GameObject.Find("SpreadSheetSetting").GetComponent<GameManaging>().setRank(resultText);    }    public void Tweet_btn() {        GameObject.Find("SpreadSheetSetting").GetComponent<GameManaging>().Tweet_btn();    }    public void GameScene() {        GameObject.Find("SpreadSheetSetting").GetComponent<GameManaging>().GameScene();    }}